"use client";

import { useState } from "react";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";

export default function Contact() {
  const [copySuccess, setCopySuccess] = useState(false);
  const [copyError, setCopyError] = useState(false);

  function handleCopyClick() {
    const textToCopy = "ryanzhang@outlook.com";
    navigator.clipboard
      .writeText(textToCopy)
      .then(() => {
        setCopySuccess(true);
        setTimeout(() => setCopySuccess(false), 2000);
      })
      .catch(() => {
        setCopyError(true);
        setTimeout(() => setCopyError(false), 2000);
      });
  }

  return (
    <>
      <div className="flex justify-center m-0">
        <h1 className="text-3xl font-extrabold tracking-tighter">contact me</h1>
      </div>

      <div className="flex justify-center m-0 relative">
        <div className="grid grid-cols-[1fr_6fr] items-center">
          <Avatar className="w-12 h-12">
            <AvatarImage src="/statics/pfp.png" />
            <AvatarFallback>Me</AvatarFallback>
          </Avatar>
          <p className="inline-block text-muted-foreground md:text-lg flex-1">
            inquiries? email me at:
            <> </>
            <a
              onClick={handleCopyClick}
              className={
                "underline tracking-tight hover:tracking-normal hover:cursor-pointer transition-all hover:text-pink-500"
              }
            >
              ryanzhang@outlook.com
            </a>{" "}
            or find me on linkedin!
          </p>
        </div>

        {copySuccess && (
          <div className="absolute top-full mt-2 bg-green-300 text-gray-600 px-4 py-2 rounded-lg shadow-lg animate-in fade-in-0 slide-in-from-top-2 duration-300">
            ✓ Email copied to clipboard!
          </div>
        )}

        {copyError && (
          <div className="absolute top-full mt-2 bg-red-500 text-white px-4 py-2 rounded-lg shadow-lg animate-in fade-in-0 slide-in-from-top-2 duration-300">
            ✗ Failed to copy email
          </div>
        )}
      </div>
    </>
  );
}

"use client";

export default function LinkCustom({ href, label }: { href: string; label: string }) {
  return (
    <a
      href={href}
      className="text-blue-700 md:text-xl sm:text-md flex-1 transition-all tracking-tighter hover:tracking-tight relative group"
      target="_blank"
      rel="noreferrer"
    >
      {label}
      <span className="block absolute bottom-0 left-0 w-0 h-[2px] bg-blue-500 transition-all duration-500 group-hover:w-full"></span>
    </a>
  );
}

"use client";

export default function NavLink({ href, label }: { href: string; label: string }) {
  return (
    <a
      href={href}
      className="relative group tracking-tighter text-sm font-medium text-gray-900 transition-all hover:tracking-tight"
    >
      {label}
      <span className="block absolute bottom-0 left-0 w-0 h-[2px] bg-pink-200 transition-all duration-500 group-hover:w-full"></span>
    </a>
  );
}
